INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'A WordPress Commenter', 'wapuu@wordpress.example', 'http://preview.ait-themes.club/citadela/', '', '2021-03-09 12:48:16', '2021-03-09 12:48:16', 'Vestibulum scelerisque enim vitae ante imperdiet ultricies. Fusce sit amet dolor rhoncus, consectetur quam sed, varius diam. Proin eu risus ullamcorper, tempor magna eget, convallis nulla. Sed finibus nibh ultrices, porta purus quis, sagittis metus. ', 0, '1', '', 'comment', 0, 0),
(2, 100, 'John Doe', 'mail@example.com', '', '', '2021-03-11 17:08:02', '2021-03-11 17:08:02', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin sem dolor, scelerisque ut diam vitae, condimentum tempor sapien. Donec vel tincidunt sem, nec euismod nulla.', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:87.0) Gecko/20100101 Firefox/87.0', 'comment', 0, 0),
(3, 100, 'admin', 'null@ait-themes.com', '', '', '2021-03-11 17:08:51', '2021-03-11 17:08:51', 'Quisque aliquam, orci non varius dapibus, felis massa convallis tellus!', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:87.0) Gecko/20100101 Firefox/87.0', 'comment', 2, 1),
(4, 86, 'Jane Smith', 'mail@example.com', '', '', '2021-03-11 17:11:36', '2021-03-11 17:11:36', 'Nullam rutrum quam vel enim sollicitudin, at ullamcorper nunc fringilla. Pellentesque lorem ante, congue et ipsum vel, sodales dignissim odio. Cras ligula metus, pellentesque nec ullamcorper sit amet, tristique quis orci.', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:87.0) Gecko/20100101 Firefox/87.0', 'comment', 0, 0);
